import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default markers in react-leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

function Dashboard() {
  const [fires, setFires] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchFires();
  }, []);

  const fetchFires = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:8001/api/fires/recent?hours=24&limit=100');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setFires(data);
      setError(null);
    } catch (err) {
      console.error('Error fetching fire data:', err);
      setError('Failed to load fire data. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const getMarkerColor = (confidence) => {
    if (confidence >= 80) return '#ff0000'; // Red - High confidence
    if (confidence >= 50) return '#ff8c00'; // Orange - Medium confidence
    return '#ffff00'; // Yellow - Low confidence
  };

  const createCustomIcon = (confidence) => {
    return L.divIcon({
      html: `<div style="background-color: ${getMarkerColor(confidence)}; width: 12px; height: 12px; border-radius: 50%; border: 2px solid white; box-shadow: 0 0 4px rgba(0,0,0,0.3);"></div>`,
      iconSize: [12, 12],
      className: 'custom-marker'
    });
  };

  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh',
        backgroundColor: '#1a1a1a',
        color: 'white'
      }}>
        <div>
          <h2>Loading PyroWatch...</h2>
          <p>Fetching real-time fire data from NASA satellites</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh',
        backgroundColor: '#1a1a1a',
        color: 'white'
      }}>
        <div>
          <h2>Error</h2>
          <p>{error}</p>
          <button onClick={fetchFires} style={{
            padding: '10px 20px',
            backgroundColor: '#ff6b6b',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer'
          }}>
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      <MapContainer 
        center={[39.8283, -98.5795]} // Center of USA
        zoom={4} 
        style={{ height: '100%', width: '100%' }}
      >
        <TileLayer
          url="https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>'
        />

        {fires.map((fire) => (
          <Marker
            key={fire.id}
            position={[fire.latitude, fire.longitude]}
            icon={createCustomIcon(fire.confidence)}
          >
            <Popup>
              <div style={{ minWidth: '200px' }}>
                <h4 style={{ margin: '0 0 10px 0', color: '#333' }}>
                  üìç Fire Detection
                </h4>
                <div style={{ fontSize: '14px', lineHeight: '1.4' }}>
                  <p><strong>Confidence:</strong> {fire.confidence.toFixed(1)}%</p>
                  <p><strong>Brightness:</strong> {fire.brightness}K</p>
                  <p><strong>Satellite:</strong> {fire.satellite}</p>
                  <p><strong>Date:</strong> {fire.acq_date} {fire.acq_time}</p>
                  {fire.frp && <p><strong>Fire Radiative Power:</strong> {fire.frp} MW</p>}
                </div>
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>

      {/* Status Bar */}
      <div style={{
        position: 'absolute',
        top: '10px',
        right: '10px',
        backgroundColor: 'rgba(0,0,0,0.8)',
        color: 'white',
        padding: '10px 15px',
        borderRadius: '5px',
        fontSize: '14px',
        zIndex: 1000
      }}>
        <div><strong>PyroWatch</strong></div>
        <div>{fires.length} active fires detected</div>
        <div style={{ fontSize: '12px', marginTop: '5px' }}>
          Last 24 hours
        </div>
      </div>

      {/* Legend */}
      <div style={{
        position: 'absolute',
        bottom: '20px',
        right: '10px',
        backgroundColor: 'rgba(0,0,0,0.8)',
        color: 'white',
        padding: '10px',
        borderRadius: '5px',
        fontSize: '12px',
        zIndex: 1000
      }}>
        <div style={{ marginBottom: '10px' }}><strong>Confidence Level</strong></div>
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '5px' }}>
          <div style={{ 
            width: '12px', 
            height: '12px', 
            backgroundColor: '#ff0000', 
            borderRadius: '50%',
            marginRight: '8px'
          }}></div>
          <span>High (‚â•80%)</span>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '5px' }}>
          <div style={{ 
            width: '12px', 
            height: '12px', 
            backgroundColor: '#ff8c00', 
            borderRadius: '50%',
            marginRight: '8px'
          }}></div>
          <span>Medium (50-80%)</span>
        </div>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <div style={{ 
            width: '12px', 
            height: '12px', 
            backgroundColor: '#ffff00', 
            borderRadius: '50%',
            marginRight: '8px'
          }}></div>
          <span>Low (&lt;50%)</span>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
